class CreateCompetences < ActiveRecord::Migration
  def up
    create_table :competences do |t|
      t.string :name
      t.integer :position
      t.text :description
      t.boolean :visible, default: true
      t.references :competence_group, index: true

      t.timestamps
    end

    Competence.create(name: "Согласование развития ИС и бизнес-стратегии", position: 1, description: "Предвидит долгосрочные потребности бизнеса, согласует инфраструктуру ИС и организационную политику. Принимает стратегические решений по развитию инфраструктуры ИС, включая стратегию использования ИТ-ресурсов.", visible: true, competence_group_id: 1)
    Competence.create(name: "Управление уровнем обслуживания", position: 2, description: "Устанавливает, проверяет и обеспечивает применимость соглашений об уровне услуг (SLA) и рамочных договоров по предоставляемым услугам. Согласует уровни оказания услуг с учетом потребностей и возможностей заказчиков и бизнеса.", visible: true, competence_group_id: 1)
    Competence.create(name: "Разработка бизнес-планов", position: 3, description: "Работает с концепциями и структурами бизнес-планов или планов продуктов, включая выявления альтернативных подходов и оценку по окупаемости инвестиций. Рассматривает возможные и применимые модели сорсинга (sourcing models).Представляет анализ стоимостных преимуществ и аргументацию по выбранной стратегии. Обеспечивает совместимость бизнес-стратегий и ИТ-стратегий. Обеспечивает информированность заинтересованных сторон о бизнес-планах, исследует политические, финансовые и организационные интересы, включая SWOT анализ.", visible: true, competence_group_id: 1)
    Competence.create(name: "Планирование проекта или продукта до утверждения", position: 4, description: "Выявляет и устанавливает текущее и целевое состояния проектов и продуктов.Оценивает эффективность затрат, рисков, возможностей, сильных и слабых стороны.Разрабатывает структурные планы, устанавливает графики и этапы. Управляет запросами на изменения. Определяет объемы поставок и готовит обзор дополнительных требований к документации. Разрабатывает правила работы с продуктами.", visible: true, competence_group_id: 1)
    Competence.create(name: "Разработка архитектуры", position: 5, description: "Детально описывает, оптимизирует, обновляет и обеспечивает применимость правила для разработки и эксплуатации архитектуры ИС. Управляет взаимоотношениями с заинтересованными сторонами со стороны бизнеса в целях обеспечения соответствия архитектуры ИС требованиям бизнеса. Выявляет потребности изменений в используемых компонентах: аппаратных, информационных и технических платформах, программном обеспечении, приложениях, процессах. Обеспечивает принятие во внимание всех аспектов операционной совместимости, масштабируемости, эксплуатационной пригодности и безопасности.", visible: true, competence_group_id: 1)
    Competence.create(name: "Разработка концепций приложений", position: 6, description: "Устанавливает наиболее подходящие ИТ-решения, согласованные с ИТ- политикой и потребностями пользователя/потребителя. Точно оценивает затраты на разработку, установку и техобслуживание предлагаемых решений. Выбирает адекватные технические параметры для проектирования решения, обеспечивая соотношение цена– качество. Идентифицирует эталонную систему проверки соответствия моделей типичным пользователям.", visible: true, competence_group_id: 1)
    Competence.create(name: "Отслеживание развития технологий", position: 7, description: "Следит за последними техническими достижениями ИТ с целью понимания развивающихся технологий. Продумывает инновационные решения по интеграции развивающихся технологий с существующими продуктами, приложениями и услугами или по разработке новых решений.", visible: true, competence_group_id: 1)
    Competence.create(name: "Устойчивое развитие", position: 8, description: "Оценивает влияние ИТ-решений с точки зрения экологической ответственности, в том числе в отношении стандартов энергопотребления. Консультирует бизнес и участников ИТ-рынка по экологически безопасным альтернативным решениям, соответствующим корпоративной стратегии. Применяет политику закупок и продаж ИТ-продуктов с учетом требований экологической ответственности. ", visible: true, competence_group_id: 1)
    Competence.create(name: "Инновации", position: 9, description: "Devises creative solutions for the provision of new concepts, ideas, products or services. Deploys novel and open thinking to envision exploitation of technological advances to address business/ society needs or research direction.", visible: true, competence_group_id: 1)

    Competence.create(name: "Разработка ПО", position: 1, description: "Проектирует и разрабатывает ПО и/или аппаратные компоненты в соответствии со спецификациями. Следует установленным методологиям для анализа и реализации требуемых компонентов и интерфейсов. Выполняет модульное и др. тестирование для обеспечения соответствия требованиям.", visible: true, competence_group_id: 2)
    Competence.create(name: "Интеграция", position: 2, description: "Устанавливает дополнительные аппаратные/программные или компоненты в существующие или предполагаемые системы. Подчиняется существующим процессам и/или процедурами (например, в управлении конфигурациями), принимая во внимание спецификации, производительность и совместимость существующих и новых компонентов в целях обеспечения целостности и операционной совместимости. Подтверждает производительность системы, официально подписывает и документирует успешную интеграцию.", visible: true, competence_group_id: 2)
    Competence.create(name: "Тестирование", position: 3, description: "Разрабатывает и проводит регулярные процедуры тестирования ИТ-систем или проверки на соблюдение требований к эксплуатационной пригодности со стороны заказчика в целях обеспечения соответствия проектным спецификациям. Обеспечивает работу новых или модернизированных компонентов и систем в соответствии с прогнозируемыми ожиданиями. Гарантирует соблюдение внутренних, внешних, государственных и международных стандартов, включая стандарты по обеспечению гигиены труда и техники безопасности, эксплуатационной пригодности, производительности, надежности и совместимости. Составляет документы и отчеты, подтверждающие соответствия сертификационным требованиям.  ", visible: true, competence_group_id: 2)
    Competence.create(name: "Ввод в эксплуатацию", position: 4, description: "Следуя предопределенным общим стандартам деятельности выполняет запланированные необходимые вмешательства в работу решений, включая действия по установке, обновлению или выводу из эксплуатации. Конфигурирует аппаратные средства, программное обеспечение или сети для обеспечения операционной совместимости компонентов системы устраняет ошибки и нестыковки, возникающие в этой связи. При необходимости привлекает сторонние квалифицированные кадры, например, независимых поставщиков сетевых услуг. Официально передает пользователю готовое к эксплуатации решение и ведет документальный учет всей необходимой информации, включая получателей оборудования, данные о конфигурации и производительности.", visible: true, competence_group_id: 2)
    Competence.create(name: "Разработка документации", position: 5, description: "Составляет документы, описывающие товары, услуги, компоненты или приложения с учетом текущих требований к оформлению документации. Выбирает стиль и средства для материалов презентаций. Создает шаблоны для систем документооборота. Обеспечивает надлежащее документирование функций и характеристик. Обеспечивает актуальность существующих документов.", visible: true, competence_group_id: 2)
    Competence.create(name: "Системотехника", position: 6, description: "Engineers software and/ or hardware components to meet solution requirements such as specifications, costs, quality, time, energy efficiency, information security and data protection. Follows a systematic methodology to analyse and build the required components and interfaces. Builds system structure models and conducts system behavior simulation. Performs unit and system tests to ensure requirements are met.", visible: true, competence_group_id: 2)

    Competence.create(name: "Поддержка пользователей", position: 1, description: "Отвечает на запросы пользователей, фиксирует релевантную информацию. Решает или эскалирует инциденты и оптимизирует производительность системы. Отслеживает результаты решения и соответсвующую удовлетвореность потребителя.", visible: true, competence_group_id: 3)
    Competence.create(name: "Поддержка изменений", position: 2, description: "Руководит процессами совершенствования ИТ-решений. Эффективно контролирует и планирует модификации программного и аппаратного обеспечения для предотвращения множественных обновлений, приводящих к непредсказуемым последствиям. Минимизирует прерывания работы системы путем внесения последовательных изменений,  соблюдая соглашения об уровне услуг.", visible: true, competence_group_id: 3)
    Competence.create(name: "Обеспечение качества", position: 3, description: "Проактивно действует для обеспечения стабильной и безопасной работы ИТ-приложений и инфраструктуры. Обновляет библиотеку эксплуатационной документации и регистрирует все события, связанные с функционированием систем. Поддерживает средства мониторинга и управления(например, скрипты, процедуры)", visible: true, competence_group_id: 3)
    Competence.create(name: "Управление проблемами", position: 4, description: "Выявляет и устраняет основные причины инцидентов. Проактивно действует для выявлении первопричин проблем ИС. Разворачивает базы знаний повторяющихся типичных ошибок.", visible: true, competence_group_id: 3)

    Competence.create(name: "Разработка стратегии информационной безопасности", position: 1, description: "Устанавливает и обеспечивает применимость организационной стратегии, охват и организационную культуру в целях обеспечения безопасности и защиты информации. Обеспечивает основу управления информационной безопасностью (ISM), в том числе для выявление ролей и отчетность (см. D.2). Использует установленные стандарты для создания требований по целостности информации, доступности и конфиденциальности данных.", visible: true, competence_group_id: 4)
    Competence.create(name: "Разработка стратегии контроля качества", position: 2, description: "Устанавливает, улучшает и дорабатывает IT-стратегию предприятия для удовлетворения потребностйе заказчиков и повышения эффективности бизнес-процессов предприятия (соблюдение баланса между стоимостью и рисками). Выявляет критически важные процессы, оказывающие влияние на услуги и характеристики продуктов для использования в системе управления качеством ИС (см. Г.4). Выявляет ответсвенность в системе контроля качества.", visible: true, competence_group_id: 4)
    Competence.create(name: "Организация обучения", position: 3, description: "Устанавливает и внедряет основные принципы ИТ- обучения, в целях определения состояния и потребности ИТ-компетенций. Структурирует, организует и составляет учебные программы и оценивает качество обучения посредством обратной связи, обеспечивает непрерывное улучшение. Адаптирует учебные планы для соответсвия изменяющимся требованиям.", visible: true, competence_group_id: 4)
    Competence.create(name: "Закупки", position: 4, description: "Применяет соответствующий  порядок осуществления закупок,  в том числе использование таких подпроцессов, как создание спецификаций,  идентификация поставщика,  анализ предложений, оценка энергетической эффективности и экологической безопасности продуктов, оценка поставщиков и их процессов, обсуждение условий контракта, выбор поставщиков и размещение контрактов. Гарантирует, что весь процесс закупки соответствует целевому назначению и увеличивает стоимость бизнеса организации.", visible: true, competence_group_id: 4)
    Competence.create(name: "Разработка товарного предложения", position: 5, description: "Разрабатывает технические предложения, отвечающие интересам клиентов, и обеспечивает торговых представителей конкурентоспособным предложением. Подчеркивает энергетическую эффективность и экологическое влияние связанных с предложением. Сотрудничает с коллегами для согласования услуги или продукти с потенциальными возможностями организации по их обеспечению.", visible: true, competence_group_id: 4)
    Competence.create(name: "Управление каналами продаж", position: 6, description: " Разрабатывает стратегию управления внешними каналами сбыта. Обеспечивает эффективную производительность канала партнеров по продажам (value-added resellers, VAR) , применяя последовательную стратегию ведения бизнеса и маркетинга. Определяет целиевой объем, географический охват и отрасли для привлечения партнеров по продажам и создает мотивационные программы для выполнения успешных продаж.", visible: true, competence_group_id: 4)
    Competence.create(name: "Управление сбытом", position: 7, description: "Обеспечивает уровень продаж посредством разработки стратегии. Демонстрирует дополнительные преимущества продуктов и услуг предприятия новым и существующим клиентам и потенциальным покупателям. Устанавливает процесс поддержки продаж, обеспечивая оперативные ответы на запросы, соответствующие стратегии и политики организации. Устанавливает системный подход ко всему процессу сбыта, в том числе к пониманию потребностей клиентов, прогнозированию, перспективной оценке, тактике ведения переговоров и завершению продаж.", visible: true, competence_group_id: 4)
    Competence.create(name: "Управление договорами", position: 8, description: "Заключает и ведет переговоры по контрактам в соответствии с организационными процессами. Гарантирует своевременные поставки, соблюдает стандарты и согласованные уровни услуг. Пернаправляет неподходящие, эскалирует значительные проблемы, составляет план действий по ликвидации последствий и, если необходимо, вносит поправки в контракты. Поддерживает целостность бюджета. Оценивает и принимает меры по соблюдению поставщиком юридических стандартов, стандартов охраны здоровья и безопасности. Активно осуществляет регулярное общение с поставщиками.", visible: true, competence_group_id: 4)
    Competence.create(name: "Развитие пресонала", position: 9, description: "Диагностирует существующие индивидуальные и групповые компетенции, выявляет потребности и нехватку навыков. Проводит анализ образовательных программ и путей развития, подбирает соответствующие методики, принимая во внимание потребности отдельных лиц и предприятия в целом. Инструктирует и/или обучает отдельных лиц или группы тому, как выявить потребностей в обучении.", visible: true, competence_group_id: 4)
    Competence.create(name: "Управление информацией и знаниями", position: 10, description: "Выявляет и управляет структурированной и неструктурированной информацией и анализирует политику распространения информации. Разрабатывает структуру информации для обеспечения возможности использования и оптимизации информации в интересах бизнеса. Понимает, какие инструменты необходимо создать, получить, сохранить, обновить и какие знания необходимо распространить для извлечения выгоды из информационного актива.", visible: true, competence_group_id: 4)
    Competence.create(name: "Needs identification", position: 11, description: "Actively listens to internal/ external customers, articulates and clarifies their needs. Manages the relationship with all stakeholders to ensure that the solution is in line with business requirements. Proposes different solutions (e.g. make-or-buy), by performing contextual analysis in support of user centered system design. Advises the customer on appropriate solution choices. Acts as an advocate engaging in the implementation or configuration process of the chosen solution.", visible: true, competence_group_id: 4)
    Competence.create(name: "Digital marketing", position: 12, description: "Understands the fundamental principles of digital marketing. Distinguishes between the traditional and digital approaches. Appreciates the range of channels available. Assesses the effectiveness of the various approaches and applies rigorous measurement techniques. Plans a coherent strategy using the most effective means available. Understands the data protection and privacy issues involved in the implementation of the marketing strategy.", visible: true, competence_group_id: 4)

    Competence.create(name: "Разработка прогнозов", position: 1, description: "Анализирует рыночные условия для оценки восприятия рынком продуктов или услуг предприятия. Оценивает возможности предприятия соответствовать будущим требованиям к продукции и качеству. Применяет соответствующие показатели для обеспечения принятия правильных решений в отношении поддержки производственных, маркетинговых, торговых и распределительных функций.", visible: true, competence_group_id: 5)
    Competence.create(name: "Управление утвержденными проектами и портфелями", position: 2, description: "Внедряет планы программ изменений. Планирует и контролирует отдельный проект или портфель ИТ-проектов в целях обеспечения координации и управления взаимозависимыми мероприятиями. Управляет проектами для разработки или внедрения новых, внутренне или внешне определенных процессов  в соответствии выявленным потребностям бизнеса. Определяет деятельность, ответственность, критические моменты, ресурсы, потребности в квалифицированных специалистах, методы взаимодействия и бюджет. Разрабатывает планы действий по решению проблем и выполнению задач в случае непредвиденных обстоятельств. Реализовывает проекты вовремя, без превышения сметы и в соответствии с исходными требованиями. Создает и сохраняет документы для контроля над ходом осуществления проекта.  ", visible: true, competence_group_id: 5)
    Competence.create(name: "Управление рисками", position: 3, description: "Осуществляет управление рисками в ИС посредством применения установленные политики и процедур организации. Оценивает риск для бизнеса организации и фиксирует потенциальный риск и планы их исключения.", visible: true, competence_group_id: 5)
    Competence.create(name: "Управление отношениями", position: 4, description: "Устанавливает и сохраняет положительные деловые отношения между клиентами и провайдером услуг (внешним или внутренним), вводя в действие и соответсвуя организационным процессам. Поддерживает постоянную связь с клиентом/партнером/поставщиком и информирует о их потребностях и управляет коммуникациями в цепочке поставок. Гарантирует, что потребности, проблемы или жалобы клиента/партнера/поставщика поняты и разрешены в соответствии с организационной политикой.", visible: true, competence_group_id: 5)
    Competence.create(name: "Улучшение процессов", position: 5, description: "Измеряет эффективность существующих ИТ-процессов. Исследует и определяет эффективность архитектуры ИТ-процессов на основе различных источников. Следует систематической методике для оценки, создания и внедрения изменений в процесс или технологии для получения значительной выгоды. Оценивает потенциальные неблагоприятные последствия изменений процессов.", visible: true, competence_group_id: 5)
    Competence.create(name: "Управление качеством", position: 6, description: "Осуществляет политику управления качеством в целях сохранения и улучшения процессов оказания услуг и производства продуктов. Планирует и устанавливает показатели управления качеством в соответствии с  ИТ-стратегией. Отслеживает показатели качества производительности и вносит рекомендации по их усовершенствованию для постоянного повышения качества.", visible: true, competence_group_id: 5)
    Competence.create(name: "Управление бизнес-требованиями", position: 7, description: "Оценивает значение новых ИТ‐решений. Определяет требования и оценивает их преимущества и размер для бизнеса. Управляет внедрением изменений,  принимая во внимание организационные и культурные особенности. Сохраняет непрерывность бизнеса и процессов посредством  внесения изменений, отслеживания их влияние, предпринимает любые необходимые действия по устранению недостатков, оптимизируя подходы.", visible: true, competence_group_id: 5)
    Competence.create(name: "Управление информационной безопасностью", position: 8, description: "Осуществляет политику обеспечения информационной безопасности. Отслеживает и принимает меры против вторжений, мошеннических действий и нарушений режима безопасности и утечки информации. Гарантирует постоянный анализ и управление рисками в сответствии с данными и информцией предприятия. Рассматривает случаи нарушения безопасности и дает рекомендации по непрерывному улучшению защиты информации.", visible: true, competence_group_id: 5)
    Competence.create(name: "Высшее руководство в сфере ИТ", position: 9, description: "Определяет, запускает в эксплуатацию и контролирует управление информационными системами в соответствии с требованиями бизнеса. Учитывает все внутренние и внешние параметры, такие как, соответствие законодательству и отраслевым стандартам, которые влияют на управление рисками и использование ресурсов, с целью достижения сбалансированных результатов деловой активности.", visible: true, competence_group_id: 5)
  end

